CREATE TABLE IF NOT EXISTS daily_summaries (
    id INTEGER PRIMARY KEY,
    date DATE UNIQUE,
    summary JSONB,
    stats JSONB
);

CREATE TABLE IF NOT EXISTS articles (
    id INTEGER PRIMARY KEY,
    url TEXT,
    date TIMESTAMP WITH TIME ZONE,
    title TEXT,
    content TEXT,
    source TEXT,
    tags JSONB,
    category TEXT,
    daily_summary_id INTEGER REFERENCES daily_summaries(id)
);

CREATE OR REPLACE VIEW view_daily_summary AS
SELECT
    id AS summary_id,
    date,
    summary AS summaries,
    stats AS categories,
    '[]'::jsonb AS "references"
FROM daily_summaries;

