INSERT INTO articles VALUES (9998, 'https://tvn24.pl/polska/z-biezni-do-bobsleja-klaudia-adamek-wraca-na-igrzyska-i-juz-przeszla-do-historii-st8871791?source=rss', (CURRENT_DATE - INTERVAL '-1 days') + TIME '11:35:15.000000', 'Z bieżni do bobsleja. Polka wraca na igrzyska i przechodzi do historii', 'Na igrzyskach już była, tych letnich, w Tokio w roku 2021. Jako sprinterka startowała tam w sztafecie 4x100 metrów. Teraz do olimpijskiej rywalizacji wraca, zimą, z pilotką Lindą Weiszewski w bobslejowych dwójkach. - Dużo zawodników i zawodniczek w tej lodowej rynnie psychicznie nie wytrzymuje, coś im się w głowie zmienia. Ja zafascynowana byłam już po pierwszym ślizgu - mówi TVN24+ Klaudia Adamek.', 'TVN24', '["najnowsze"]', '', ) ON CONFLICT (id) DO NOTHING;
