INSERT INTO articles VALUES (10036, 'https://wydarzenia.interia.pl/zagranica/news-miedwiediew-mowil-o-konflikcie-globalnym-nie-jestesmy-szalen,nId,22546087', (CURRENT_DATE - INTERVAL '-1 days') + TIME '07:23:04.000000', 'Miedwiediew mówił o konflikcie globalnym. "Nie jesteśmy szaleni"', 'Nie jesteśmy zainteresowani konfliktem globalnym. Nie jesteśmy szaleni - stwierdził w nowym wywiadzie Dmitrij Miedwiediew, zaznaczając przy tym jednak, że takiego scenariusza "nie da się wykluczyć". Były prezydent Federacji Rosyjskiej ocenił też, że "próg bólu wydaje się obniżać", jeśli chodzi o zagrożenia na świecie. Odniósł się także do rzekomego ryzyka ze strony Rosji i Chin dla Grenlandii.', 'Interia', '["fakty"]', '', ) ON CONFLICT (id) DO NOTHING;
