INSERT INTO articles VALUES (6505, 'https://tvn24.pl/szczecin/szczecin-prom-jantar-unity-zostal-ochrzczony-w-pierwszy-rejs-wyplynie-20-stycznia-st8853430?source=rss', (CURRENT_DATE - INTERVAL '15 days') + TIME '09:12:55.000000', '"Polska potęga na Bałtyku" ochrzczona. Prom Jantar Unity popłynie już 20 stycznia', 'W Szczecinie został zaprezentowany nowy prom Jantar Unity, który wkrótce rozpocznie regularne kursy na linii Świnoujście - Trelleborg (Szwecja). Prom zbudowała Gdańska Stocznia Remontowa - Remontowa Holding. W sobotę po południu odbył się chrzest statku. Jantar Unity to pierwszy od ponad 30 lat prom zbudowany dla polskiego armatora - Unity Line. Nasza reporterka była na pokładzie.', 'TVN24', '["najnowsze"]', 'Economy', 8) ON CONFLICT (id) DO NOTHING;
