INSERT INTO articles VALUES (9202, 'https://tvn24.pl/swiat/usa-ohio-plug-sniezny-utorowal-droge-karetce-wiozacej-malego-chlopczyka-do-szpitala-w-cincinnati-nagranie-st8873569?source=rss', (CURRENT_DATE - INTERVAL '4 days') + TIME '18:44:17.000000', 'Pług śnieżny utorował drogę karetce wiozącej małego chłopca do szpitala', 'Potężna burza śnieżna od ubiegłego piątku paraliżuje życie Amerykanów. W stanie Ohio małe dziecko potrzebowało natychmiastowej interwencji lekarzy pediatrów. Drogę ambulansowi utorował pług śnieżny.', 'TVN24', '["najnowsze"]', 'Society', 20) ON CONFLICT (id) DO NOTHING;
